insert into PLMS_USER_ROLE T ( ) SELECT SEQ_PLMS_USER_ROLE.nextval , TT.* from (
select #{item.userId,jdbcType=VARCHAR}, #{item.roleId,jdbcType=BIGINT}, #{item.isActive,jdbcType=CHAR}, #{item.createdBy,jdbcType=VARCHAR}, #{item.createTime,jdbcType=TIMESTAMP}, #{item.updatedBy,jdbcType=VARCHAR}, #{item.updateTime,jdbcType=TIMESTAMP}, #{item.recVer,jdbcType=BIGINT}, #{item.tagSeq,jdbcType=BIGINT} from dual
) TT
